import { Component } from '@angular/core';

@Component({
  selector: 'app-formulario',
  templateUrl: './formulario.component.html',
  styleUrls: ['./formulario.component.css']
})
export class FormularioComponent {
  formData: any = {};

  submitForm(form: any) {
    if (form.valid) {
      // Processar os dados do formulário aqui
      console.log(this.formData);
    } else {
      alert('Por favor, preencha todos os campos obrigatórios.');
    }
  }
}
<h1>3.Cadastro de Aluno</h1>
<div class="container">
    <form #form="ngForm" (ngSubmit)="submitForm(form)">
        <div class="form-group">
          <label for="ra">RA:</label>
          <input type="text" id="ra" name="ra" [(ngModel)]="formData.ra" class="form-control" required>
        </div>
    
        <div class="form-group">
          <label for="nome">Nome:</label>
          <input type="text" id="nome" name="nome" [(ngModel)]="formData.nome" class="form-control" required>
        </div>
    
        <div class="form-group">
          <label for="email">Email:</label>
          <input type="email" id="email" name="email" [(ngModel)]="formData.email" class="form-control" required>
        </div>
    
        <div class="form-group">
          <label for="celular">Celular:</label>
          <input type="tel" id="celular" name="celular" [(ngModel)]="formData.celular" class="form-control" required>
        </div>
    
        <button type="submit" class="btn btn-primary" [disabled]="form.invalid">Enviar</button>
      </form>

</div>
