import { Component, OnInit } from '@angular/core';
import { UsuarioService } from '../services/usuario.service';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { UsuarioDetalhesComponent } from './usuario-detalhes/usuario-detalhes.component';

@Component({
  selector: 'app-usuario',
  templateUrl: './usuario.component.html',
  styleUrls: ['./usuario.component.css']
})
export class UsuarioComponent implements OnInit {
  usuarios: any = [];
  constructor(private usuarioService : UsuarioService,
  private modalService: NgbModal){}
  
  obterUsuario(){
    return this.usuarioService.obterUsuarios();
  }
  ngOnInit(): void {
      this.obterUsuario().subscribe((data:any) => {
        this.usuarios = data;
      });
  }
  obterUsuarios(){
    return this.usuarioService.obterUsuarios();
  }
  abrirModal(){
    const modalRef = this.modalService.open(UsuarioDetalhesComponent);
    modalRef.result.then((retornoModal:any) => {
      this.obterUsuarios().subscribe((data:any) =>{
        this.usuarios = data;
      })
    })

  }

}
<div class="row">
    <div class="col-md-12">
        <button class="btn btn-primary float-end" (click)="abrirModal()">Adicionar</button>
    </div>
</div>

<div class="row">
    <div class="col-md-12">
        <table class="table table-striped table-hover">
            <thead>
                <tr>
                    <th>id</th>
                    <th>Nome</th>
                    <th>Email</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let usu of usuarios">
                    <td>{{ usu.id }}</td>
                    <td>{{ usu.nome }}</td>
                    <td>{{ usu.email }}</td>
                </tr>
            </tbody>
        </table>
    </div>
</div>
