import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-seguro',
  templateUrl: './seguro.component.html',
  styleUrls: ['./seguro.component.css']
})
export class SeguroComponent {

  nomeSegurado: string = '';
  sexo: string = '';
  idade: number = 0;
  valorAutomovel: number = 0;
  valorApolice: number = 0;

  calcularApolice() {
    if (this.sexo === 'masculino') {
      if (this.idade <= 25) {
        this.valorApolice = 0.15 * this.valorAutomovel;
      } else {
        this.valorApolice = 0.10 * this.valorAutomovel;
      }
    } else if (this.sexo === 'feminino') {
      this.valorApolice = 0.08 * this.valorAutomovel;
    } 
  }
}
<div class="container">
    <form>
      <div class="form-group">
        <label for="nomeSegurado">Nome do Segurado</label>
        <input type="text" class="form-control" [(ngModel)]="nomeSegurado" name="nomeSegurado" required>
      </div>
  
      <div class="form-group">
        <label for="sexo">Sexo</label>
        <select class="form-control" [(ngModel)]="sexo" name="sexo" required>
          <option value="masculino">Masculino</option>
          <option value="feminino">Feminino</option>
        </select>
      </div>
  
      <div class="form-group">
        <label for="idade">Idade</label>
        <input type="number" class="form-control" [(ngModel)]="idade" name="idade" required>
      </div>
  
      <div class="form-group">
        <label for="valorAutomovel">Valor do Automóvel</label>
        <input type="number" class="form-control" [(ngModel)]="valorAutomovel" name="valorAutomovel" required>
      </div>
  
      <button type="button" class="btn btn-primary" (click)="calcularApolice()">Calcular Apólice</button>
  
      <div *ngIf="valorApolice > 0" class="mt-3">
        <h4>Valor da Apólice: {{ valorApolice | currency:'BRL' }}</h4>
      </div>
    </form>
  </div>
